/* eslint-disable no-use-before-define */
import React, { useState, useContext } from 'react';
import { useSelector } from 'react-redux';
import PropTypes from 'prop-types';
import styled, { css } from 'styled-components';
import { NavLink, Link } from 'react-router-dom';
import heartIcon from 'assets/icons/small-heart.svg';
import cartIcon from 'assets/icons/shopping-cart1.png';
import userIcon from 'assets/icons/user.png';
import searchIcon from 'assets/icons/search.svg';
import hamburgerMenuIcon from 'assets/icons/bars.svg';
import ButtonIcon from 'components/atoms/ButtonIcon/ButtonIcon';
import Search from 'components/atoms/Search/Search';
import { NavigationContext } from 'context/NavigationContext';
import logo from 'assets/images/IMG-20221229-WA0041.jpg';
import Modal from 'react-modal';

const TopnavWrapper = styled.nav`
  position: fixed;
  width: 100%;
  height: 65px;
  z-index: ${({ theme }) => theme.zIndex.level5};
  background-color: ${({ theme }) => theme.white};
  box-shadow: 0 -1px #ddd inset;
  transition: 0.3s ease-in-out;
  display: flex;
  justify-content: center;
  padding: 0 5px 0 0;
  ${({ theme }) => theme.mq.lg} {
    height: 85px;
  }
  ${({ theme }) => theme.mq.md} {
    ${({ transparent }) =>
      transparent &&
      css`
        & {
          background-color: transparent;
          box-shadow: 0 -1px rgba(255, 255, 255, 0.25) inset;
        }
        & ${LinkItem} {
          color: white;
          &:after {
            border-color: ${({ theme }) => theme.black};
          }
          &.active:after {
            transform: scaleX(0);
          }
        }
        & ${Logo} {
          background-image: url(${logo});
        }
        & ${ButtonIcon} {
          filter: invert(0);
        }
        & ${Option}:hover {
          background-color: ${({ theme }) => theme.white100T};
        }
        & ${Badge} {
          background-color: ${({ theme }) => theme.white};
          color: ${({ theme }) => theme.dark};
        }
      `};
  }
`;

const InnerWrapper = styled.div`
  width: 100%;
  max-width: 1500px;
  display: flex;
  align-items: center;
  justify-content: center;
  ${({ theme }) => theme.mq.md} {
    padding: 0 15px;
  }
`;

const LinkItemsWrapper = styled.div`
  flex: 3;
  display: flex;
`;

const LinkItem = styled(NavLink)`
  display: none;
  margin: 0 15px;
  position: relative;
  padding: 33px 0;
  width: 85px;
  text-align: center;
  font-family: ${({ theme }) => theme.fonts.subFont};
  letter-spacing: 1px;
  transition: 0.3s ease-in-out;
  color: ${({ theme }) => theme.dark};
  text-decoration: none;
  ${({ theme }) => theme.mq.lg} {
    display: block;
  }
  &:after {
    content: '';
    display: block;
    position: absolute;
    left: 0;
    bottom: 0;
    width: 100%;
    border-bottom: 5px solid ${({ theme }) => theme.blue};
    transform: scaleX(0);
    transform-origin: 0% 50%;
    transition: transform 250ms ease-in-out;
  }
  &:hover:after,
  &.active:hover:after,
  &.active:after {
    transform: scaleX(1);
  }
`;

const Logo = styled(NavLink)`
  display: block;
  width: 150px;
  height: 100%;
  background: url(${logo}) no-repeat center;
  background-size: 100%;
  ${({ theme }) => theme.mq.lg} {
    width: 190px;
  }
`;

const OptionsWrapper = styled(LinkItemsWrapper)`
  justify-content: flex-end;
`;

const Option = styled.div`
  display: ${({ visible }) => (visible ? 'flex' : 'none')};
  margin: 0 8px;
  position: relative;
  padding: 10px;
  border-radius: 50%;
  transition: 0.2s;
  cursor: pointer;
  &:hover {
    background-color: ${({ theme }) => theme.white100};
  }
  ${({ theme }) => theme.mq.lg} {
    display: ${({ menu }) => (menu ? 'none' : 'flex')};
  }
  ${({ isActive }) =>
    isActive &&
    css`
      background-color: ${({ theme }) => theme.white100T};
    `}
`;

const Badge = styled.span`
  display: block;
  position: absolute;
  width: 20px;
  height: 20px;
  line-height: 21px;
  top: -3px;
  right: -7px;
  border-radius: 50%;
  text-align: center;
  font-size: ${({ theme }) => theme.fontSize.xxs};
  background-color: ${({ theme }) => theme.blue};
  color: ${({ theme }) => theme.white};
  transition: 0.3s ease-in-out;
  font-weight: ${({ theme }) => theme.medium};
  box-shadow: 0 0 8px 0 rgba(0, 0, 0, 0.2);
`;

const WishlistBadge = styled(Badge)`
  top: -5px;
`;

const Topnav = ({ isTransparent }) => {
  const [isSearchVisible, setSearchVisibility] = useState(false);
  const { openSidenav, openSideCart } = useContext(NavigationContext);

  const cartItemsNumber = useSelector(({ cart }) =>
    cart.reduce((acc, { quantity }) => acc + quantity, 0),
  );
  const wishlistItemsNumber = useSelector(({ wishlist }) => wishlist.length);

  return (
    <TopnavWrapper transparent={isTransparent}>
      <InnerWrapper>
        <LinkItemsWrapper>
          <Option menu visible onClick={openSidenav}>
            <ButtonIcon icon={hamburgerMenuIcon} />
          </Option>
          <LinkItem exact activeclass="active" to="/">
            Home
          </LinkItem>
          <LinkItem activeclass="active" to="/catalog">
            Catalogue
          </LinkItem>
        </LinkItemsWrapper>
        <Logo to="/" />
        <OptionsWrapper>
          <Option
            isActive={isSearchVisible}
            onMouseEnter={() => setSearchVisibility(true)}
            onMouseLeave={() => setSearchVisibility(false)}
          >

            <Search
              topnav
              isVisible={isSearchVisible}
              closeSearch={() => setSearchVisibility(false)}
            />
            <ButtonIcon icon={searchIcon} />
          </Option>
          <Option as={Link} to="/wishlist">
            <ButtonIcon icon={heartIcon} />
            {wishlistItemsNumber !== 0 && (
              <WishlistBadge>{wishlistItemsNumber}</WishlistBadge>
            )}
          </Option>


          <Option visible onClick={openSideCart}>
            <ButtonIcon icon={cartIcon} />
            {cartItemsNumber !== 0 && <Badge>{cartItemsNumber}</Badge>}
          </Option>
        </OptionsWrapper>
      </InnerWrapper>
    </TopnavWrapper>
  );
};

Topnav.propTypes = {
  isTransparent: PropTypes.bool.isRequired,
};

export default Topnav;
