import React, { Fragment, useEffect, useState } from 'react';
import './AllProducts.css';
import Products from '../Product/Product';
import Loader from '../Loader/Loader';
import { useDispatch, useSelector } from 'react-redux';
import { getProduct } from '../../actions/productAction';
import Pagination from 'react-js-pagination';
import Slider from '@material-ui/core/Slider';
import Typography from '@material-ui/core/Typography';
import {useAlert} from 'react-alert';
import {clearErrors} from '../../actions/productAction';

const categories = [
    "Men's",
    "Shoes",
    "Tops",
    "Women's",
    "Trousers",
    "Gym & Fitness"
]

const AllProducts = ({match}) => {
    const dispatch = useDispatch();

    const alert = useAlert()

    const [currentPage, setCurrentPage] = useState(1);
    const [category, setCategory] = useState("")

    const [rating, setRating] = useState(0)


    const {products,loading,error, productsCount, resultPerPage} = useSelector(
        (state) => state.products
    );

    const keyword = match.params.keyword;

    const setCurrentPageNo = (e) => {
        setCurrentPage(e)
    }



    useEffect(() => {
        if(error) {
            alert.error(error);
            dispatch(clearErrors())
        }
        dispatch(getProduct(keyword,currentPage,category,rating));
    }, [dispatch, keyword,currentPage,category,rating,alert,error]);

    //let count = filteredProductsCount;

  return (
  <Fragment>
    {loading ? (
    <Loader /> 
    ): (
    <Fragment>
        <h2 className='productsHeading'>Products</h2>

        <div className='products'>
            {products &&
            products.map((product) => (
                <Products key={product._id} product={product} />
            ))}
        </div>

        {}


        <div className='filterBox'>
            <Typography>Categories</Typography>
            <ul className="categoryBox">
                {categories.map((category) => (
                    <li
                    className='category-link'
                    key={category}
                    onClick={() => setCategory(category)}
                    >
                        {category}
                    </li>
                ))}
            </ul>

            <fieldset>
                <Typography component="legend">Ratings Above</Typography>
                <Slider
                value={rating}
                onChange={(e,newRating) => {
                    setRating(newRating);
                }}
                aria-labelledby="continuous-slider"
                min={0}
                max={5}
                valueLabelDisplay="auto" />
            </fieldset>

        </div>


        </Fragment>
        )}
  </Fragment>
  );
};

export default AllProducts;
