return (
<LoginWrapper>

<FormWrapper onSubmit={handleSubmit}>
{isLogin ? (
<>
  <FormInput type="email" placeholder="Enter your email" />
  <FormInput type="password" placeholder="Enter your password" />
  <SubmitButton onSubmit={handleSubmit}>Login</SubmitButton>
  <p>
    Don't have an account? <a onClick={() => setIsLogin(false)}>Register</a>
  </p>
</>
) : (
<>
<FormInput type="text" placeholder="Name" />
<FormInput type="email" placeholder="E-mail" />
<FormInput type="password" placeholder="Password" />
<FormInput type="password" placeholder="Confirm password" />
<SubmitButton>Register</SubmitButton>
<p>
Already have an account? <a onClick={() => setIsLogin(true)}>Login</a>
</p>
</>
)}
</FormWrapper>
</LoginWrapper>
)}
}
export default LoginView
