import React, { Fragment, useEffect } from "react";
import "./orderDetails.css";
import { useSelector, useDispatch } from "react-redux";
import MetaData from "../layout/MetaData";
import { Link } from "react-router-dom";
import { Typography } from "@material-ui/core";
import { getOrderDetails, clearErrors } from "../../actions/orderAction";
import Loader from "../Loader/Loader";
import { useAlert } from "react-alert";

const OrderDetails = ({ match }) => {
  const { orderDetails: { order, error, loading }, user } = useSelector((state) => state);

  const dispatch = useDispatch();
  const alert = useAlert();

  useEffect(() => {
    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

    dispatch(getOrderDetails(match.params.id));
  }, [dispatch, alert, error, match.params.id]);
  return (
    <Fragment>
      {loading ? (
        <Loader />
      ) : (
        <Fragment>
          <MetaData title="Order Details" />
          <div className="orderDetailsPage">
            <div className="orderDetailsContainer">
            <Typography component="h1">
               {order ? `Order #${order._id}` : 'Loading...'}
            </Typography>

              <Typography>Shipping Info</Typography>
              <div className="orderDetailsContainerBox">
                <div>
                  <p>Name:</p>
                  <span>{user && user.name}</span>
                </div>
                <div>
                  <p>Phone:</p>
                  <span>
                  {order && order.shippingInfo && order.shippingInfo.phoneNo}
                  </span>
                </div>
                <div>
                  <p>Address:</p>
                  <span>
                  {order && order.shippingInfo &&
                      `${order.shippingInfo.address}, ${order.shippingInfo.city}, ${order.shippingInfo.state}, ${order.shippingInfo.pinCode}, ${order.shippingInfo.country}`}
                  </span>
                </div>
              </div>
              <Typography>Payment</Typography>
              <div className="orderDetailsContainerBox">
                <div>
                <p
  className={
    order && order.paymentInfo && order.paymentInfo.status === "succeeded"
      ? "greenColor"
      : "redColor"
  }
>
  {order && order.paymentInfo
    ? order.paymentInfo.status === "succeeded"
      ? "PAID"
      : "NOT PAID"
    : "Loading..."}
</p>

                </div>

                <div>
                  <p>Amount:</p>
                  <span>{order && order.totalPrice && order.totalPrice}</span>
                </div>
              </div>

              <Typography>Order Status</Typography>
              <div className="orderDetailsContainerBox">
                <div>
                { order && (
  <p
    className={
      order.paymentInfo &&
      order.paymentInfo.status === "succeeded"
        ? "greenColor"
        : "redColor"
    }
  >
    {order.paymentInfo && order.paymentInfo.status}
  </p>
) }


                </div>
              </div>
            </div>

            <div className="orderDetailsCartItems">
              <Typography>Order Items:</Typography>
              <div className="orderDetailsCartItemsContainer">
  {order && order.orderItems &&
    order.orderItems.map((item) => (
      <div key={item.product}>
        <img src={item.image} alt="Product" />
        <Link to={`/product/${item.product}`}>
          {item.name}
        </Link>{" "}
        <span>
          {item.quantity} X ${item.price} ={" "}
          <b>${item.price * item.quantity}</b>
        </span>
      </div>
    ))}
</div>

            </div>
          </div>
        </Fragment>
      )}
    </Fragment>
  );
};

export default OrderDetails;
