import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import SocialMedia from '../atoms/SocialMedia/SocialMedia';
import Accordion from './Accordion';
import useMedia from '../../hooks/useMedia';
import {
  aboutLinks,
  servicesLinks,
  infoLinks,
} from './links';

const Wrapper = styled.footer`
  background-color: rgb(111, 179, 235);


`;

const InnerWrapper = styled.div`
  margin: 0 auto;
  max-width: 1500px;
  padding: 15px 15px 0;

`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  ${({ theme }) => theme.mq.md} {
    flex-direction: row;
    justify-content: space-between;
    padding: 40px 0;
  }
`;

const SocialMediaWrapper = styled.div`
  display: flex;
  padding: 20px 0;
`;

const StyledSocialMedia = styled(SocialMedia)`
  margin-right: 10px;
  &:hover {
    filter: brightness(200%);
  }
`;

const RightsWrapper = styled.div`
  display: flex;
  padding: 20px 0;
  border-top: 1px solid #303030;
`;

const Rights = styled.span`
  margin: 0 auto;

`;

const RightsLinksWrapper = styled.div`
  display: none;
  margin-left: auto;

`;

const RightsLink = styled(Link)`
  color: ${({ theme }) => theme.gray100};
  transition: 0.15s;
  margin-left: 15px;
  text-decoration: none;
  &:hover {
    color: ${({ theme }) => theme.white};
  }
`;

const Footer = () => {
  const matches = useMedia('(min-width: 768px)');

  return (
    <Wrapper>
      <InnerWrapper>
        <Content>
          <Accordion active={matches} title="About Us" links={aboutLinks} />
          <Accordion
            active={matches}
            title="Our Services"
            links={servicesLinks}
          />
          <Accordion active={matches} title="Information" links={infoLinks} />
          <SocialMediaWrapper>
            <StyledSocialMedia type="twitter" />
            <StyledSocialMedia type="facebook" />
            <StyledSocialMedia type="instagram" />
          </SocialMediaWrapper>
        </Content>
        <RightsWrapper>
          <Rights>&copy; 2023 TaylorGoods - All Rights Reserved</Rights>
          <RightsLinksWrapper>
            <RightsLink to="/">Terms of Sale</RightsLink>

          </RightsLinksWrapper>
        </RightsWrapper>
      </InnerWrapper>
    </Wrapper>
  );
};

export default Footer;
