import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

import './Login.css';

const LoginWrapper = styled.div `display: flex; flex-direction: column; align-items: center; justify-content: center; height: 100vh; width: 100%;;`

const FormWrapper = styled.div `display: flex; flex-direction: column; align-items: center; padding: 2rem; background-color: #ffffff; box-shadow: 0px 0px 10px #00000029; border-radius: 10px; width: 80%; @media (min-width: 768px) { width: 50%; };`

const FormInput = styled.input `padding: 1.5rem; margin-bottom: 1rem; border-radius: 5px; border: none; border-bottom: 1px solid #00000029; font-size: 1.5rem; &::placeholder { color: black }; width: 250px;`

const FormLabel = styled.label `font-size: 1.5rem; margin-bottom: 0.5rem;;`

const SubmitButton = styled.button `width:100px;height:40px;padding: 0.75rem 2rem; background-color: rgb(111, 179, 235); color: white; border-radius: 5px; border: none; font-size: 1.2rem; margin-top: 1rem; cursor: pointer; &:hover { background-color: rgb(157, 201, 238) };`

const LoginView = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState({});

  const handleSubmit = e => {
    e.preventDefault();
    //submit logic here
    setUser({ name: "John Doe", email: "johndoe@example.com" });
  };

return (
<LoginWrapper>

<FormWrapper onSubmit={handleSubmit}>
{isLogin ? (
<>

<FormInput type="email" placeholder="Enter your email" />
<FormInput type="password" placeholder="Enter your password" />
<SubmitButton>Login</SubmitButton>
<p>
Don't have an account? <a onClick={() => setIsLogin(false)}>Register</a>
</p>
</>
) : (
<>
<FormInput type="text" placeholder="Name" />
<FormInput type="email" placeholder="E-mail" />
<FormInput type="password" placeholder="Password" />
<FormInput type="password" placeholder="Confirm password" />
<SubmitButton>Register</SubmitButton>
<p>
Already have an account? <a onClick={() => setIsLogin(true)}>Login</a>
</p>
</>
)}
</FormWrapper>
</LoginWrapper>
)}

export default LoginView
