import React, { useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { setSearchValues } from 'actions';
import styled from 'styled-components';
import Button from 'components/atoms/Button/Button';
import { useState } from 'react';

const Wrapper = styled.section`
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-wrap: wrap;
  flex-direction: column;
  ${({ theme }) => theme.mq.sm} {
    flex-direction: row;
  }
  ${({ theme }) => theme.mq.xl} {
    flex-wrap: nowrap;
    justify-content: center;
  }
`;
const Category = styled.div `flex-basis: 100%; position: relative; transition: 0.2s; margin-top: 12px; overflow: hidden; ${({ theme }) => theme.mq.sm} { margin: 10px; flex-basis: calc(50% - 20px); &:nth-child(1) { flex-basis: 100%; max-height: 200px; ${({ theme }) => theme.mq.md} { max-height: 320px; } } } ${({ theme }) => theme.mq.xl} { flex-basis: 31.5% !important; max-height: 100% !important; };`

const CategoryImage = styled.img `display: block; width: 100%; height: 100%; transition: transform 0.7s;`

const StyledButton = styled(Button)`
position: absolute;
bottom: 5%;
left: 50%;
transform: translate(-50%, -5%);
z-index: 2;
box-shadow: 1px 1px 0 0 rgba(0, 0, 0, 0.1);
transition: 0.35s;
opacity: 0.85;
font-weight: ${({ theme }) => theme.medium};
border-radius: 2px;
text-transform: none;
padding: 18px 40px !important;`



const dispatch = useDispatch();

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
        try {
            const { data } = await axios.get('http://localhost:5000/api/categories');
            setCategories(data.categories);
            setImages(data.images);
        } catch (error) {
            console.log(error);
        }
    };
    fetchData();
  }, []);

  return (
    <Wrapper>
      {categories.map((category, index) => (
        <Category key={category}>
          <StyledLink
            onClick={() =>
              dispatch(setSearchValues(category, 'category'))
            }
            to={`/products?category=${category}`}
          >
            <CategoryImage src={images[index]} alt={category} />
            <StyledButton>{category}</StyledButton>
          </StyledLink>
        </Category>
      ))}
    </Wrapper>
  );
};

export default Categories;

