import React, { useState } from 'react';
import styled from 'styled-components';

const LoginWrapper = styled.div `display: flex; flex-direction: column; align-items: center; justify-content: center; height: 100vh; width: 100%;;`

const FormWrapper = styled.div `display: flex; flex-direction: column; align-items: center; padding: 2rem; background-color: #ffffff; box-shadow: 0px 0px 10px #00000029; border-radius: 10px; width: 80%; @media (min-width: 768px) { width: 50%; };`

const FormInput = styled.input `padding: 0.5rem; margin-bottom: 1rem; border-radius: 5px; border: none; border-bottom: 1px solid #00000029; font-size: 1rem; &::placeholder { color: #00000029; };`

const FormLabel = styled.label `font-size: 1.2rem; margin-bottom: 0.5rem;;`

const SubmitButton = styled.button `padding: 0.75rem 2rem; background-color: #4CAF50; color: white; border-radius: 5px; border: none; font-size: 1.2rem; margin-top: 1rem; cursor: pointer; &:hover { background-color: #3e8e41; };`

const LoginView = () => {
const [isLogin, setIsLogin] = useState(true);

const handleSubmit = e => {
e.preventDefault();
//submit logic here
};

return (
<LoginWrapper>
<FormWrapper onSubmit={handleSubmit}>
{isLogin ? (
<>
<FormLabel>Email</FormLabel>
<FormInput type="email" placeholder="Enter your email" />
<FormLabel>Password</FormLabel>
<FormInput type="password" placeholder="Enter your password" />
<SubmitButton>Login</SubmitButton>
<p>
Don't have an account? <a onClick={() => setIsLogin(false)}>Register</a>
</p>
</>
) : (
<>
<FormLabel>Email</FormLabel>
<FormInput type="email" placeholder="Enter your email" />
<FormLabel>Password</FormLabel>
<FormInput type="password" placeholder="Enter your password" />
<FormLabel>Confirm Password</FormLabel>
<FormInput type="password" placeholder="Confirm your password" />
<SubmitButton>Register</SubmitButton>
<p>
Already have an account? <a onClick={() => setIsLogin(true)}>Login</a>
</p>
</>
)}
</FormWrapper>
</LoginWrapper>
)}

export default Lo
