import React, { Fragment, useEffect, useState } from 'react'
import "./ProductDetails.css";
import Carousel from 'react-material-ui-carousel';
import {useSelector, useDispatch} from 'react-redux';
import { getProductDetails, clearErrors } from '../../actions/productAction';
import {addItemsToCart} from '../../actions/cartAction';
import ReactStars from 'react-rating-stars-component'; 
import ReviewCard from './ReviewCard';
import Loader from '../Loader/Loader';
import {useAlert} from 'react-alert';
import MetaData from '../layout/MetaData'
import { Button, Dialog, DialogActions, DialogContent, DialogTitle } from '@material-ui/core';
import { Rating } from '@material-ui/lab';

const ProductDetails = ({match}) => {

  const dispatch = useDispatch();
  const alert = useAlert();

  const {product, loading, error} = useSelector(state => state.productDetails)

  useEffect(() => {
    if(error) {
      return alert.error(error)
      dispatch(clearErrors())
    }
    dispatch(getProductDetails(match.params.id))
  }, [dispatch, match.params.id,error, alert])


  const options = {
    edit: false,
    color: "rgba(20,20,20,0.1)",
    activeColor: "tomato",
    size: window.innerWidth< 600 ? 20 :25,
    value: product.ratings,
    isHalf:true,
}
  const [quantity, setQuantity] = useState(1);
  const [open, setOpen] = useState(false);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  const increasequantity = () => {
    if(product.Stock <= quantity) return;

    const qty = quantity + 1;
    setQuantity(qty);
  }

  const decreasequantity = () => {
    if(1 >= quantity) return;
    const qty = quantity - 1;
    setQuantity(qty);
  }

  const addToCartHandler = () => {
    dispatch(addItemsToCart(match.params.id, quantity));
    alert.success("Item added to Cart");
  }
  const submitReviewTogge = () => {
    open ? setOpen(false) : setOpen(true);
  }

  const reviewSubmitHandler = () => {
    const myForm = new FormData();

    myForm.set("rating", rating);
    myForm.set("comment", comment);
    myForm.set("productId", match.params.id);

    disp
  }

  return (
    <Fragment>
      <MetaData title={product.name} />
      {loading ? <Loader /> : (
        <Fragment>
        <div className='ProductDetails'>
          <div>
            <Carousel>
              {product.images && product.images.map((item, i) => (
                <img 
                 className='CarouselImage'
                 key={item.url}
                 src={item.url}
                 alt={`${i} Slide`}
                />
              ))}
            </Carousel>
          </div>
          <div>
            <div className="detailsBlock-1">
              <h2>{product.name}</h2>
              <p>Product # {product._id}</p>
            </div>
            <div className="detailsBlock-2">
              <ReactStars {...options} />
              <span>({product.numOfReviews} Reviews)</span>
            </div>
            <div className="detailsBlock-3">
              <h1>{`$${product.price}`}</h1>
              <div className="detailBlock-3-1">
                <div className="detailsBlock-3-1-1">
                  <button onClick={decreasequantity}>-</button>
                  <input readOnly value={quantity} type="number" />
                  <button onClick={increasequantity}>+</button>
                </div> {" "}
                <button disabled={product.Stock < 1 ? true : false} className="submitReview" onClick={addToCartHandler}>Add to Cart</button>
              </div>
              <p>
                Status: {" "}
                <b className={product.Stock < 1 ? "redColor":"greenColor"}>
                  {product.Stock < 1 ? "OutOfStock" : "InStock"}
                </b>
              </p>
            </div>
            <div className="detailsBlock-4">
              Description : <p>{product.description}</p>
            </div>
            <button onClick={submitReviewTogge} className="submitReview">Submit Review</button>
          </div>
        </div>
        <h3 className="reviewsHeading">REVIEWS</h3>
        <Dialog
        aria-labelledby='simple-dialog-title'
        open={open}
        onClose={submitReviewTogge}
        >
          <DialogTitle>
            Submit Review
          </DialogTitle>
          <DialogContent className='submitDialog'>
            <Rating
            onChange={(e) => setRating(e.target.value)} value={rating} size='large' />
            <textarea
            className='submitDialogTextArea'
            cols='30'
            rows="5"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            ></textarea>
          </DialogContent>
          <DialogActions>
            <Button onClick={submitReviewTogge} color="secondary">Cancel</Button>
            <Button onClick={submitReviewTogge} color="primary">Submit</Button>
          </DialogActions>

        </Dialog>

  {product.reviews && product.reviews[0] ? (
    <div className="reviews">
      {product.reviews && 
      product.reviews.map((review) => <ReviewCard review={review} />)}
    </div>
  ) : (
    <p className="noReviews">No Reviews Yet</p>
  )}  
      </Fragment>
      )}
    </Fragment>
  )
}

export default ProductDetails;
