const LoginView = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [user, setUser] = useState({});

  const handleSubmit = async e => {
    e.preventDefault();
    const name = nameRef.current.value;
    const email = emailRef.current.value;
    const password = passwordRef.current.value;
    const confirmPassword = confirmPasswordRef.current.value;

    let endpoint = "http://localhost:5000/api";
    if (isLogin) {
      endpoint = '/login';
      try {
        const response = await axios.post(endpoint + '/login', { email: email.value, password: password.value });

        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          // navigate to dashboard or home page
        }
      } catch (error) {
        console.error(error);
      }
    } else {
      endpoint = '/register';
      try {
        const response = await axios.post(endpoint + '/register', { name: name.value, email: email.value, password: password.value, confirmPassword: confirmPassword.value });

        if (response.data.token) {
          localStorage.setItem('token', response.data.token);
          // navigate to dashboard or home page
        }
      } catch (error) {
        console.error(error);
      }
    }
  }

  const nameRef = React.createRef();
const emailRef = React.createRef();
const passwordRef = React.createRef();
const confirmPasswordRef = React.createRef();


  return (
    <div>
      <form onSubmit={handleSubmit}>
        {isLogin ? (
          <>
            <input type="email" placeholder="Enter your email" name="email" />
            <input type="password" placeholder="Enter your password" name="password" />
            <button type="submit">Login</button>
            <p>
              Don't have an account? <button type="button" onClick={() => setIsLogin(false)}>Register</button>
            </p>
          </>
        ) : (
          <>
            <input type="text" placeholder="Name" ref={nameRef} name="name" />
<input type="email" placeholder="E-mail" ref={emailRef} name="email" />
<input type="password" placeholder="Password" ref={passwordRef} name="password" />
<input type="password" placeholder="Confirm password" ref={confirmPasswordRef} name="confirmPassword" />

            <button type="submit">Register</button>
            <p>
              Already have an account? <button type="button" onClick={() => setIsLogin(true)}>Login</button>
            </p>
          </>
        )}
      </form>
    </div>
  );
}

export default LoginView;
